import React, { useEffect } from "react"
import { Button, Row, Table } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import Loader from "../../components/loader/Loader"
import Message from "../../components/message/Message"

const ProductsListPage = ({ history }) => {
  const dispatch = useDispatch()

  const { userInfo } = useSelector((state) => state.userReducer)
  const { loading, products, errMessage } = useSelector((state) => state.productsReducer)

  const deleteHandler = (userId) => {
    // dispatch()
  }
  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(fetchProductsAscync())
    } else {
      history.push("/login")
    }
  }, [dispatch, userInfo])
  return (
    <>
      <Row className="align-items-center">
        <Col>
          <h1>Products</h1>
        </Col>
        <Col className="text-right">
          <Button className="my-3" onClick={createProductHandler}>
            <i className="fas fa-plus"> </i> Create Product
          </Button>
        </Col>
      </Row>

      {loading ? (
        <Loader />
      ) : errMessage ? (
        <Message variant="danger">{usersListErrorMessage}</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm">
          <thead>
            <th>ID</th>
            <th>name</th>
            <th>Price</th>
            <th>Category</th>
            <th>Brand</th>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id}>
                <td> {product._id}</td>
                <td> {product.name}</td>
                <td>{product.price}</td>
                <td>{product.category}</td>
                <td>{product.brand}</td>
                <td>
                  <Button
                    className="btn-sm"
                    onClick={() => {
                      history.push(`/admin/product/${product._id}/edit`)
                    }}
                  >
                    <i className="fas fa-edit mx-1"></i>
                  </Button>
                  <Button
                    className="btn-sm mx-1"
                    onClick={() => {
                      deleteHandler(product._id)
                    }}
                  >
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </>
  )
}

export default ProductsListPage
