import Axios from "axios"
import productTypes from "./productTypes"
const fitchProductStart = () => {
  return {
    type: productTypes.FETCH_PRODUCT_START,
  }
}
const fitchProductSuccess = (products) => {
  return {
    type: productTypes.FETCH_PRODUCT_SUCCESS,
    payload: products,
  }
}
const fitchProductfail = (error) => {
  return {
    type: productTypes.FETCH_PRODUCT_FAIL,
    payload: error,
  }
}

export const fetchProductAscync = (id) => {
  return (dispatch) => {
    dispatch(fitchProductStart())
    const query = `
     query getProductById($id: String!) {
        getProductById(id : $id) {
         _id
         name
         image
         brand
         price
         rating
         numReviews
         category
         description
         countInStock


       }
     }
     `
    Axios({
      method: "POST",
      data: {
        query,
        variables : {id }
      },
      url: "/graphql",
    })
      .then((res) => {
        dispatch(fitchProductSuccess(res.data.data.getProductById))
      })
      .catch((data) => {
        console.log(data);
        dispatch(fitchProductfail("product error"))
      })
  }
}
