import productTypes from "./productsTypes"

const INTIAL_STATE = { products: [], loading: false, errMessage: "" }

const productReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case productTypes.FETCH_PRODUCT_START:
      return { ...state, loading: true }
    case productTypes.FETCH_PRODUCT_SUCCESS:
      return { ...state, loading: false, products: action.payload, errMassage: null }

    case productTypes.FETCH_PRODUCT_FAIL:
      return { ...state, loading: false, errMassage: action.payload }
    default:
      return state
  }
}
export default productReducer
