import React from "react";
import { Form } from "react-bootstrap";
import "./createProduct.scss";
const CreateProduct = () => {
  return (
    <div className="create-product">
      <h2>create new product</h2>
      <section className="product-info">
        <Row>
          <Col md={6}>
            <h2> User Profile </h2>
            {userProfileErrorMessage ? (
              <Message variant="danger">{userProfileErrorMessage} </Message>
            ) : (
              userProfileErrorMessage && (
                <Message variant="danger">{userProfileErrorMessage} </Message>
              )
            )}
            {getUserProlieFetchState && <Loader />}
            <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="name"
                  placeholder="Enter name"
                  name="name"
                  value={name}
                  onChange={userInfoChange}
                />
              </Form.Group>
              <Form.Group controlId="email">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter email"
                  name="email"
                  value={email}
                  onChange={userInfoChange}
                />
              </Form.Group>
              <Form.Group controlId="newPassword">
                <Form.Label>newPassword</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter newPassword"
                  name="newPassword"
                  value={newPassword}
                  onChange={userInfoChange}
                />
              </Form.Group>
              <Form.Group controlId="oldPassword">
                <Form.Label>oldPassword</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter oldPassword"
                  name="oldPassword"
                  value={oldPassword}
                  onChange={userInfoChange}
                  required
                />
              </Form.Group>
              <Button type="submit"> update</Button>
            </Form>
          </Col>
        </Row>
      </section>
    </div>
  );
};

export default CreateProduct;
