import React, { useState, useEffect } from "react"
import { Button, Col, Form, Row } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import Message from "../../components/message/Message"
import Loader from "../../components/loader/Loader"
import { getUserProfileForAdmin } from "../../redux/user/admin/adminAction"
const UserProfileForAdmin = ({ match }) => {
  const dispatch = useDispatch()
  const { UserForAdminError, getUserFetchState, UserForAdmin } = useSelector(
    (state) => state.adminReducer
  )
  useEffect(() => {
      if(!UserForAdmin?.name){

          dispatch(getUserProfileForAdmin(match.params.userid))
      }else{
        setUserInfo((preval) => ({
            ...preval,
            email: UserForAdmin.email,
            name: userProfileInfo.name,
          }))
      }
  }, [])

  const [userInfoInput, setUserInfo] = useState({
    email: "",
    isAdmin: false,
    name: "",
    adminPassword: "",
  })
  const { email, name, adminPassword } = userInfoInput

  const userInfoChange = (event) => {
    const { name, value } = event.target
    setUserInfo((preval) => {
      return { ...preval, [name]: value }
    })
  }

  const submitHandler = (event) => {
    event.preventDefault()
    dispatch()
  }
  return (
    <>
      {getUserFetchState ? (
        <Loader />
      ) : UserForAdminError ? (
        <Message variant="danger">{UserForAdminError}</Message>
      ) : (
        <Row>
          <Col md={6}>
            <h2> User Profile </h2>

            <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="name"
                  placeholder="Enter user name"
                  name="name"
                  value={name}
                  onChange={userInfoChange}
                />
              </Form.Group>
              <Form.Group controlId="email">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter user email"
                  name="email"
                  value={email}
                  onChange={userInfoChange}
                />
              </Form.Group>
              <Form.Group controlId="adminPassword">
                <Form.Label>Enter your password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Enter your password"
                  name="adminPassword"
                  value={adminPassword}
                  onChange={userInfoChange}
                  required
                />
              </Form.Group>
              <Button type="submit"> update</Button>
            </Form>
          </Col>
        </Row>
      )}
    </>
  )
}

export default UserProfileForAdmin
