import React, { useState } from "react";
import { Button, Col, Form, Row } from "react-bootstrap";
import Message from "../../components/message/Message";
import "./createProduct.scss";
const CreateProduct = ({ history }) => {
  const [productInfo, changeProductInfo] = useState({
    name: "",
    price: "",
    image: "",
    prand: "",
    description: "",
    countInStuck: "",
  });
  const { name, price, image, prand, description, countInStuck } = productInfo;
  const submitHandler = () => {};
  const productInfoOnChange = (event) => {};

  return (
    <div className="create-product">
      <h2>create new product</h2>
      <section className="product-info">
        <Row>
          <Col md={6}>
            <Form onSubmit={submitHandler}>
              <Form.Group controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter name"
                  name="name"
                  value={name}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Form.Group controlId="price">
                <Form.Label>Email address</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter price"
                  name="price"
                  value={price}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Form.Group controlId="image">
                <Form.Label>image</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter image"
                  name="image"
                  value={image}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Form.Group controlId="prand">
                <Form.Label>oldPassword</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter prand"
                  name="prand"
                  value={prand}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Form.Group controlId="description">
                <Form.Label>oldPassword</Form.Label>
                <Form.Control
                  type="description"
                  placeholder="Enter description"
                  name="description"
                  value={description}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Form.Group controlId="prand">
                <Form.Label>oldPassword</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter prand"
                  name="prand"
                  value={prand}
                  onChange={productInfoOnChange}
                />
              </Form.Group>
              <Button type="submit"> update</Button>
            </Form>
          </Col>
        </Row>
      </section>
    </div>
  );
};

export default CreateProduct;
