import React, { useState, useEffect } from "react"
import { Button, Col, Form, Row } from "react-bootstrap"
import { Link } from "react-router-dom"
import { useDispatch, useSelector } from "react-redux"
import Message from "../../components/message/Message"
import Loader from "../../components/loader/Loader"

const ProfilePage = ({ location, history }) => {
  const dispatch = useDispatch()
  const { userInfo } = useSelector((state) => state.userReducer)
  const [userInfoInput, setUserInfo] = useState({
    email: "",
    newPassword: "",
    name: "",
    oldPassword: "",
  })
  const [message, setMessage] = useState(null)
  const { email, password, name, confirmPassword } = userInfoInput
  const redirect = location.search ? location.search.split("=")[1] : "/"

  const userInfoChange = (event) => {
    const { name, value } = event.target
    setUserInfo((preval) => {
      return { ...preval, [name]: value }
    })
  }
  useEffect(() => {
    if (!userInfo) {
      history.push(redirect)
    }
  }, [history, userInfo])
  const submitHandler = (event) => {
    event.preventDefault()
    if (password !== confirmPassword) {
      setMessage("password did't match ")
      return
    }
    setMessage(null)
  }
  return (
    <Row>
      <Col md={3}>
        <h2> User Profile </h2>
        {message ? (
          <Message variant="danger">{message} </Message>
        ) : (
          errorMassage && <Message variant="danger">{errorMassage} </Message>
        )}
        {fetchState && <Loader />}
        <Form onSubmit={submitHandler}>
          <Form.Group controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="name"
              placeholder="Enter name"
              name="name"
              value={name}
              onChange={userInfoChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="email">
            <Form.Label>Email address</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              name="email"
              value={email}
              onChange={userInfoChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="newPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="newPassword"
              placeholder="Enter newPassword"
              name="newPassword"
              value={newPassword}
              onChange={userInfoChange}
              required
            />
          </Form.Group>
          <Form.Group controlId="newPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="newPassword"
              placeholder="Enter newPassword"
              name="newPassword"
              value={newPassword}
              onChange={userInfoChange}
              required
            />
          </Form.Group>
          <Button type="submit"> update</Button>
        </Form>
      </Col>
      <Col md={9}></Col>
    </Row>
  )
}

export default ProfilePage
