import React, { useState, useEffect } from "react"
import { Form } from "react-bootstrap"
import { Link } from "react-router-dom"
import FormContainer from "../../components/FormContainer/FormContainer"
import { login } from "../../redux/user/userAction"
const LoginPage = () => {
  const [userInfo, setUserInfo] = useState({ rmail: "", password: "" })
    const {email ,password } = userInfo
    const userInfoChange = (event)=>{
        const {name , value} = event.target
        setUserInfo(preval =>{
            return {...preval , [name]:value }
        } )
    }
  return (
    <FormContainer>
      <h1>Sign in</h1>
      <Form>
        <Form.Group controlId="email">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="enter email" name="email" value={email} onChange={userInfoChange}  /> 
        </Form.Group>
        <Form.Group controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" placeholder="enter password" name="password" value={password} onChange={userInfoChange} /> 
        </Form.Group>
      </Form>
    </FormContainer>
  )
}

export default LoginPage
