import Axios from "axios"
import orderTypes from "./orderTypes"

export const createOrder = (cart) => async (dispatch, getState) => {
  const {
    orderItems,
    shippingAddress,
    paymentMethode,
    taxPrice,
    shippingPrice,
    totalPrice,
    itemsPrice,
  } = cart
  console.log(cart)
  try {
    console.log(cart)
    const token = getState().userReducer.userInfo.token
    dispatch({
      type: orderTypes.CREATE_ORDER_START,
    })
    const query = `
      mutation($orderItems : String!
        $shippingAddress : String!
        $paymentMethode : String! 
        $taxPrice : Float!
        $shippingPrice: Float!
        $totalPrice: Float!
        $itemsPrice : Float!){
        createOrder($orderItems : $orderItems
            $shippingAddress :  $shippingAddress
            $paymentMethode : $paymentMethode
            $taxPrice : $taxPrice
            $shippingPrice: $shippingPrice
            $totalPrice:  $totalPrice
            $itemsPrice : $itemsPrice )
            {
                _id
            }
          }
        `
    const variables = {}
    const { data } = await Axios({
      method: "POST",
      url: "/graphql",
      data: {
        query,
        variables,
      },
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
    dispatch({
      type: orderTypes.CREATE_ORDER_SUCCESS,
      payload: data.data.updateUserProfile,
    })
  } catch (error) {
    const errorMessage = error.response.data.errors
      ? error.response.data.errors[0].message
      : error.response.data
      ? error.response.data
      : "error happend"
    dispatch({
      type: orderTypes.CREATE_ORDER_FAIL,
      payload: errorMessage,
    })
  }
}
