import Axios from "axios"
import productTypes from "./productsTypes"
import {useD} from "redux"
const fitchProductStart = () => {
  return {
    type: productTypes.FETCH_PRODUCT_START,
  }
}
const fitchProductSuccess = (products) => {
  return {
    type: productTypes.FETCH_PRODUCT_SUCCESS,
    payload: products,
  }
}
const fitchProductfail = (error) => {
  return {
    type: productTypes.FETCH_PRODUCT_START,
    payload: error,
  }
}

export const fetchProductsAscync = () => {
  return (dispatch) => {
    dispatch(fitchProductStart())
    const query = `
     query{
       getAllProducts {
         _id
         name
         image
         brand
         price
         rating
         numReviews
         category
         description
         countInStock


       }
     }
     `
    Axios({
      method: "POST",
      data: {
        query,
      },
      url: "/graphql",
    })
      .then((res) => {
        dispatch(fitchProductSuccess(res.data.data.getAllProducts))
      })
      .catch((error) => {
        dispatch(fitchProductfail(error))
      })
  }
}
