import userProfilTypes from "./userProfileTypes"
const INTIAL_STATE = {
  getUserProlieFetchState: false,
  userProfileInfo: {},
  errorMessage: "",
}

const userProfileReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    //case get user profile
    case userProfilTypes.USER_GET_PROFILE_START:
      return {
        ...state,
        userProfile: { getUserProlieFetchState: true },
      }
    case userProfilTypes.USER_GET_PROFILE_SUCCESS:
      return { getUserProlieFetchState: false, userProfileInfo: action.payload }

    case userProfilTypes.USER_GET_PROFILE_FAIL:
      localStorage.setItem("userInfo", null)
      return { ...state, userProfileErrorMessage: false, userProfileInfo: action.payload }

    default:
      return state
  }
}

export default userProfileReducer
