import Axios from "axios"
import orderTypes from "./orderTypes"

export const createOrder = (cart) => async (dispatch, getState) => {
  const {
    cartItems,
    shippingAddress,
    paymentMethod,
    taxPrice,
    shippingPrice,
    totalPrice,
    itemsPrice,
  } = cart
  try {
    const token = getState().userReducer.userInfo.token
    dispatch({
      type: orderTypes.CREATE_ORDER_START,
    })
    const query = `
      mutation createOrder(
        $orderItems : String!,
        $shippingAddress : String!,
        $paymentMethod : String! ,
        $taxPrice : Float!,
        $shippingPrice: Float!,
        $totalPrice: Float!,
        $itemsPrice : Float!,
        )
        {
            createOrder(
                orderItems : $orderItems,
                shippingAddress :  $shippingAddress,
                paymentMethod : $paymentMethod,
                taxPrice : $taxPrice,
                shippingPrice: $shippingPrice,
                totalPrice:  $totalPrice,
                itemsPrice : $itemsPrice
                 )
                {
                    _id
                }
        }
        `
    console.log(cart)
    const variables = {
      orderItems: JSON.stringify(cartItems),
      shippingAddress:JSON.stringify(shippingAddress),
      paymentMethod,
      taxPrice,
      shippingPrice,
      totalPrice,
      itemsPrice:itemsPrice,
    }
    const { data } = await Axios.post("/graphql",{
      query,
      variables,
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
    dispatch({
      type: orderTypes.CREATE_ORDER_SUCCESS,
      payload: data.data.updateUserProfile,
    })
  } catch (error) {
    const errorMessage = error.response.data.errors
      ? error.response.data.errors[0].message
      : error.response.data
      ? error.response.data
      : "error happend"
    dispatch({
      type: orderTypes.CREATE_ORDER_FAIL,
      payload: errorMessage,
    })
  }
}
