import Axios from "axios"
import productsTypes from "./productsTypes"
const fitchProductStart = () => {
  return {
    type: productTypes.FETCH_PRODUCTS_START,
  }
}
const fitchProductSuccess = (products) => {
  return {
    type: productsTypes.FETCH_PRODUCTS_SUCCESS,
    payload: products,
  }
}
const fitchProductfail = (error) => {
  return {
    type: productsTypes.FETCH_PRODUCTS_FAIL,
    payload: error,
  }
}

export const fetchProductsAscync = () => {
  return (dispatch) => {
    dispatch(fitchProductStart())
    const query = `
     query{
       getAllProducts {
         _id
         name
         image
         brand
         price
         rating
         numReviews
         category
         description
         countInStock


       }
     }
     `
    Axios({
      method: "POST",
      data: {
        query,
      },
      url: "/graphql",
    })
      .then((res) => {
        dispatch(fitchProductSuccess(res.data.data.getAllProducts))
      })
      .catch((data) => {
        console.log(data);
        dispatch(fitchProductfail("some error"))
      })
  }
}
