import userTypes from "./userTypes"

const userInfo = localStorage.getItem("")
const INTIAL_STATE = {
  userInfo: {},
  fetchState: false,
  errorMassage: "",
}

const userReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case userTypes.USER_LOGIN_START:
      return {
        ...state,
        fetchState: true,
      }
    case userTypes.USER_LOGIN_SUCCESS:
      return { ...state, userInfo: action.payload, errorMassage: "", fetchState: false }

    case userTypes.USER_LOGIN_FAIL:
      return { errorMassage: action.payload, fetchState: false, userInfo: {} }
    case userTypes.LOG_OUT:
      return { errorMassage: "", fetchState: false, userInfo: {} }
    default:
      return state
  }
}

export default userReducer
