import React, { useState, useEffect } from "react"
import { Button, Col, Form, Row } from "react-bootstrap"
import { useDispatch, useSelector } from "react-redux"
import Message from "../../components/message/Message"
import Loader from "../../components/loader/Loader"

const UserProfileForAdmin = ({ location, history }) => {
  const dispatch = useDispatch()
  const { userInfo } = useSelector((state) => state.userReducer)

  const [editProfile, setEditProfile] = useState(false)
  const [userInfoInput, setUserInfo] = useState({
    email: "",
    isAdmin: false,
    name: "",
    adminPassword: "",
  })
  const { email, name, adminPassword  } = userInfoInput

  const userInfoChange = (event) => {
    const { name, value } = event.target
    setUserInfo((preval) => {
      return { ...preval, [name]: value }
    })
  }

  const submitHandler = (event) => {
    event.preventDefault()
    dispatch()
    setEditProfile(false)
  }
  return (
    <div>
      <Row>
        <Col md={6}>
          <h2> User Profile </h2>

          <Form onSubmit={submitHandler}>
            <Form.Group controlId="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="name"
                placeholder="Enter user name"
                name="name"
                value={name}
                onChange={userInfoChange}
              />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter user email"
                name="email"
                value={email}
                onChange={userInfoChange}
              />
            </Form.Group>
            <Form.Group controlId="adminPassword">
              <Form.Label>Enter your password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                name="oldPassword"
                value={adminPassword}
                onChange={userInfoChange}
                required
              />
            </Form.Group>
            <Button type="submit"> update</Button>
          </Form>
        </Col>
      </Row>
    </div>
  )
}

export default UserProfileForAdmin
